var $reg=/[0-9A-Za-z]{3,20}/i;$(function(){$("input").val("");$(".rippleria").rippleria({duration:750,easing:"linear",color:"#d6d6d6"});$("i.input-feedback").each(function(){var a=0,f=0;var c=$("#"+$(this).attr("icon-for"));var g=parseInt(c.css("padding-top"),10),d=parseInt(c.css("padding-bottom"),10),e=parseInt(c.css("padding-left"),10),b=parseInt(c.css("padding-right"),10);a=c.position().top+((c.height()+g+d)-$(this).height())/2;f=c.position().left+(c.width()+e+b)-$(this).width()-5;$(this).css({top:a,left:f});$(this).show()});$("input").on("keypress",function(a){if(a.keyCode==13){$("#btn_login").trigger("click")}});$("#btn_login").on("click",function(){var a=$.trim($("#id").val());var b=$.trim($("#pwd").val());if(a.match($reg)===null){if(!$("#id").hasClass("error")){$("#id").addClass("error")}}else{$("#id").removeClass("error")}if(b.match($reg)===null){if(!$("#pwd").hasClass("error")){$("#pwd").addClass("error")}}else{$("#pwd").removeClass("error")}if(!$("input").hasClass("error")){$.post("func.php",{func:"AdminLogin",id:a,pwd:b},function(c){if(c.OK==1){window.location.href="content.html"}else{$("input").val("");alert("登入帳號或密碼錯誤, 請重新輸入")}},"json")}})});